mapper.select:
SELECT

 from mmall_user user
WHERE user.username=#{username}
and password=#{password}

SELECT user.question
  from mmall_user user
where user.username=#{username}

SELECT count(1)
from mmall_user user
WHERE
user.username=#{username}
and
user.question=#{question}
and user.answer=#{answer}

SELECT count(1)
from mmall_user
WHERE
id=#{userId}
and password=#{password}

SELECT 

from mmall_user
WHERE

    id=#{id}


   and  username= #{username}



    and email=#{email}


    and phone=#{phone}


    and question=#{question}


    and answer=#{answer}


    and role=#{role}


    and createTime=#{createTime}


    and updateTime=#{updateTime}
